    public void parseJson(JSONObject object){
        if (object!=null){
            items = new ArrayList<>();
            try {
                JSONArray array = object.getJSONArray("products");
                for (int i = 0; i < array.length(); i++) {
                    JSONObject json = array.getJSONObject(i);
                    ProductsOfCategory item = new ProductsOfCategory();
                    item.parseJson(json);
                    items.add(item);
                }
            }catch (Exception e){
                e.printStackTrace();
            }
        }
    }

